import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CareerFairForm = () => {
  // Form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    fieldOfStudy: '',
    motivation: '',
    cvFile: null,
    cvFileName: '',
    internshipInterest: false,
    hasValidVisa: false,
    germanLanguageConfirmed: false
  });

  // UI State
  const [companies, setCompanies] = useState([]);
  const [selectedCompanies, setSelectedCompanies] = useState({});
  const [availableSlots, setAvailableSlots] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  const fieldOfStudyOptions = [
    'Computer Science',
    'Software Engineering',
    'Data Science',
    'Cybersecurity',
    'Information Technology',
    'Business Administration',
    'Marketing',
    'Finance',
    'Mechanical Engineering',
    'Electrical Engineering',
    'Civil Engineering',
    'Biomedical Engineering',
    'Medicine',
    'Nursing',
    'Psychology',
    'Education',
    'Law',
    'Other'
  ];

  // Fetch companies on component mount
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/career-fair/companies');
        setCompanies(response.data.data);
        setError(null);
      } catch (err) {
        setError('Failed to load companies. Please try again later.');
        console.error('Error fetching companies:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchCompanies();
  }, []);

  // Fetch available slots when company is selected
  const handleCompanySelect = async (companyId) => {
    try {
      const response = await axios.get(`/api/career-fair/available-slots/${companyId}`);
      const company = response.data.data;

      setSelectedCompanies(prev => ({
        ...prev,
        [companyId]: {
          selected: true,
          company: company
        }
      }));

      setAvailableSlots(prev => ({
        ...prev,
        [companyId]: company.timeSlots
      }));

      setError(null);
    } catch (err) {
      if (err.response?.data?.maintenance) {
        setError(`${err.response.data.message}`);
      } else {
        setError('Failed to load available slots. Please try again.');
      }
      console.error('Error fetching slots:', err);
    }
  };

  // Handle company deselection
  const handleCompanyDeselect = (companyId) => {
    setSelectedCompanies(prev => {
      const updated = { ...prev };
      delete updated[companyId];
      return updated;
    });

    setAvailableSlots(prev => {
      const updated = { ...prev };
      delete updated[companyId];
      return updated;
    });
  };

  // Handle timeslot selection
  const handleSlotSelect = (companyId, timeSlot) => {
    setSelectedCompanies(prev => ({
      ...prev,
      [companyId]: {
        ...prev[companyId],
        selectedSlot: timeSlot
      }
    }));
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Handle CV file upload
  const handleCVUpload = (e) => {
    const file = e.target.files[0];
    
    if (!file) {
      setFormData(prev => ({
        ...prev,
        cvFile: null,
        cvFileName: ''
      }));
      return;
    }

    // Validate file type
    const validTypes = ['application/pdf', 'application/msword', 
                       'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
    const validExtensions = ['.pdf'];
    
    const fileExtension = '.' + file.name.split('.').pop().toLowerCase();
    
    if (!validTypes.includes(file.type) && !validExtensions.includes(fileExtension)) {
      setError('Please upload a valid CV file (PDF)');
      e.target.value = '';
      return;
    }

    // Validate file size (max 5MB)
    const maxSizeInMB = 5;
    if (file.size > maxSizeInMB * 1024 * 1024) {
      setError(`CV file must be smaller than ${maxSizeInMB}MB`);
      e.target.value = '';
      return;
    }

    setFormData(prev => ({
      ...prev,
      cvFile: file,
      cvFileName: file.name
    }));
    setError(null);
  };

  // Handle special company field changes
  const handleCompanyFieldChange = (companyId, fieldName, value) => {
    setSelectedCompanies(prev => ({
      ...prev,
      [companyId]: {
        ...prev[companyId],
        [fieldName]: value
      }
    }));
  };

  // Validate form
  const validateForm = () => {
    if (!formData.firstName.trim()) {
      setError('First name is required');
      return false;
    }
    if (!formData.lastName.trim()) {
      setError('Last name is required');
      return false;
    }
    if (!formData.email.trim()) {
      setError('Email is required');
      return false;
    }
    if (!formData.phoneNumber.trim()) {
      setError('Phone number is required');
      return false;
    }
    if (!formData.fieldOfStudy) {
      setError('Field of study is required');
      return false;
    }
    if (!formData.motivation.trim()) {
      setError('Motivation is required');
      return false;
    }
    if (!formData.cvFile) {
      setError('CV file is required');
      return false;
    }

    // Validate at least one company is selected
    const selectedCount = Object.values(selectedCompanies).filter(c => c.selected && c.selectedSlot).length;
    if (selectedCount === 0) {
      setError('Please select at least one company and timeslot');
      return false;
    }

    // Validate special rules
    for (const [companyId, company] of Object.entries(selectedCompanies)) {
      if (!company.selected) continue;

      // Check ÖBB German requirement
      if (company.company.specialRules.germanRequired && !company.germanLanguageConfirmed) {
        setError('ÖBB requires B2 level German. Please confirm.');
        return false;
      }

      // Check Netconomy internship requirement
      if (company.company.specialRules.internshipVisa && company.company.specialRules.internshipVisa) {
        if (!company.internshipInterest || !company.hasValidVisa) {
          setError('Netconomy requires confirmation of internship interest and valid visa.');
          return false;
        }
      }
    }

    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!validateForm()) {
      return;
    }

    try {
      setSubmitting(true);

      // Build FormData for file upload
      const submitData = new FormData();
      
      // Add text fields
      submitData.append('firstName', formData.firstName);
      submitData.append('lastName', formData.lastName);
      submitData.append('email', formData.email);
      submitData.append('phoneNumber', formData.phoneNumber);
      submitData.append('fieldOfStudy', formData.fieldOfStudy);
      submitData.append('motivation', formData.motivation);
      submitData.append('cv', formData.cvFile);
      submitData.append('internshipInterest', formData.internshipInterest);
      submitData.append('hasValidVisa', formData.hasValidVisa);
      submitData.append('germanLanguageConfirmed', formData.germanLanguageConfirmed);

      // Build appointments array
      const appointments = Object.entries(selectedCompanies)
        .filter(([_, company]) => company.selected && company.selectedSlot)
        .map(([companyId, company]) => ({
          companyId,
          timeSlot: company.selectedSlot
        }));

      submitData.append('appointments', JSON.stringify(appointments));

      const response = await axios.post('/api/career-fair/register', submitData, {
        headers: {
          'Content-Type': 'multipart/form-data'
        }
      });

      if (response.data.success) {
        setSuccess(true);
        setFormData({
          firstName: '',
          lastName: '',
          email: '',
          phoneNumber: '',
          fieldOfStudy: '',
          motivation: '',
          cvFile: null,
          cvFileName: '',
          internshipInterest: false,
          hasValidVisa: false,
          germanLanguageConfirmed: false
        });
        setSelectedCompanies({});
        setAvailableSlots({});

        // Reset file input
        const fileInput = document.getElementById('cvInput');
        if (fileInput) fileInput.value = '';

        // Show success message for 5 seconds
        setTimeout(() => setSuccess(false), 5000);
      }
    } catch (err) {
      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('Failed to register. Please try again.');
      }
      console.error('Error submitting form:', err);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return (
      <div className="container" style={{ textAlign: 'center', padding: '60px 20px' }}>
        <div className="spinner"></div>
        <p style={{ marginTop: '20px', color: '#666' }}>Loading companies...</p>
      </div>
    );
  }

  return (
    <div className="container" style={{ maxWidth: '1200px', margin: '0 auto', padding: '40px 20px' }}>
      {/* Header Section */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h1 style={{ color: '#f29b20', marginBottom: '10px' }}>🎯 Career Fair Registration</h1>
        <p style={{ fontSize: '1.1em', color: '#666' }}>Register for interviews with our partner companies</p>
      </div>

      <form onSubmit={handleSubmit} style={{
        background: 'white',
        borderRadius: '12px',
        padding: '40px',
        boxShadow: '0 10px 40px rgba(242, 155, 32, 0.1)'
      }}>
        {/* Error Message */}
        {error && (
          <div style={{
            padding: '15px 20px',
            borderRadius: '6px',
            marginBottom: '20px',
            backgroundColor: '#f8d7da',
            color: '#721c24',
            border: '1px solid #f5c6cb'
          }}>
            {error}
          </div>
        )}

        {/* Success Message */}
        {success && (
          <div style={{
            padding: '15px 20px',
            borderRadius: '6px',
            marginBottom: '20px',
            backgroundColor: '#d4edda',
            color: '#155724',
            border: '1px solid #c3e6cb'
          }}>
            ✅ Registration successful! Check your email for confirmation.
          </div>
        )}

        {/* Student Information Section */}
        <section style={{ marginBottom: '40px' }}>
          <h2 style={{
            fontSize: '1.5em',
            color: '#333',
            marginBottom: '20px',
            paddingBottom: '10px',
            borderBottom: '2px solid #f29b20'
          }}>
            📋 Your Information
          </h2>

          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr 1fr',
            gap: '20px',
            marginBottom: '20px'
          }}>
            <div>
              <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
                First Name *
              </label>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
                maxLength="50"
                placeholder="Enter your first name"
                style={{
                  padding: '12px',
                  border: '2px solid #e0e0e0',
                  borderRadius: '6px',
                  fontSize: '1em',
                  fontFamily: 'inherit',
                  transition: 'border-color 0.3s, box-shadow 0.3s',
                  width: '100%',
                  boxSizing: 'border-box'
                }}
              />
            </div>
            <div>
              <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
                Last Name *
              </label>
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
                maxLength="50"
                placeholder="Enter your last name"
                style={{
                  padding: '12px',
                  border: '2px solid #e0e0e0',
                  borderRadius: '6px',
                  fontSize: '1em',
                  fontFamily: 'inherit',
                  transition: 'border-color 0.3s, box-shadow 0.3s',
                  width: '100%',
                  boxSizing: 'border-box'
                }}
              />
            </div>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: '1fr 1fr',
            gap: '20px',
            marginBottom: '20px'
          }}>
            <div>
              <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
                Email *
              </label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                placeholder="your.email@example.com"
                style={{
                  padding: '12px',
                  border: '2px solid #e0e0e0',
                  borderRadius: '6px',
                  fontSize: '1em',
                  fontFamily: 'inherit',
                  transition: 'border-color 0.3s, box-shadow 0.3s',
                  width: '100%',
                  boxSizing: 'border-box'
                }}
              />
            </div>
            <div>
              <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
                Phone Number *
              </label>
              <input
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
                placeholder="+43 123 456789"
                style={{
                  padding: '12px',
                  border: '2px solid #e0e0e0',
                  borderRadius: '6px',
                  fontSize: '1em',
                  fontFamily: 'inherit',
                  transition: 'border-color 0.3s, box-shadow 0.3s',
                  width: '100%',
                  boxSizing: 'border-box'
                }}
              />
            </div>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
              Field of Study *
            </label>
            <select
              name="fieldOfStudy"
              value={formData.fieldOfStudy}
              onChange={handleInputChange}
              required
              style={{
                padding: '12px',
                border: '2px solid #e0e0e0',
                borderRadius: '6px',
                fontSize: '1em',
                fontFamily: 'inherit',
                width: '100%',
                boxSizing: 'border-box'
              }}
            >
              <option value="">Select your field of study</option>
              {fieldOfStudyOptions.map(field => (
                <option key={field} value={field}>{field}</option>
              ))}
            </select>
          </div>

          <div style={{ marginBottom: '20px' }}>
            <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
              Motivation / Cover Letter *
            </label>
            <textarea
              name="motivation"
              value={formData.motivation}
              onChange={handleInputChange}
              required
              maxLength="1000"
              rows="5"
              placeholder="Tell us why you're interested in the career fair..."
              style={{
                padding: '12px',
                border: '2px solid #e0e0e0',
                borderRadius: '6px',
                fontSize: '1em',
                fontFamily: 'inherit',
                resize: 'vertical',
                minHeight: '120px',
                width: '100%',
                boxSizing: 'border-box'
              }}
            />
            <small style={{ color: '#999', marginTop: '5px', display: 'block' }}>
              {formData.motivation.length}/1000 characters
            </small>
          </div>

          {/* CV Upload Section */}
          <div style={{ marginBottom: '20px' }}>
            <label style={{ fontWeight: '600', marginBottom: '8px', display: 'block', color: '#333' }}>
              Upload CV * 📄
            </label>
            <div style={{
              border: '2px dashed #f29b20',
              borderRadius: '8px',
              padding: '20px',
              textAlign: 'center',
              backgroundColor: '#fffbf0',
              cursor: 'pointer',
              transition: 'all 0.3s'
            }}>
              <input
                id="cvInput"
                type="file"
                accept=".pdf,.doc,.docx,application/pdf,application/msword,application/vnd.openxmlformats-officedocument.wordprocessingml.document"
                onChange={handleCVUpload}
                style={{ display: 'none' }}
              />
              <label htmlFor="cvInput" style={{ cursor: 'pointer', display: 'block' }}>
                <p style={{ margin: '0 0 10px', color: '#333', fontSize: '1.1em', fontWeight: '600' }}>
                  📤 Click to upload or drag & drop
                </p>
                <p style={{ margin: '0', color: '#999', fontSize: '0.9em' }}>
                  PDF • Max 5MB
                </p>
              </label>
              {formData.cvFileName && (
                <div style={{
                  marginTop: '15px',
                  padding: '10px',
                  backgroundColor: '#d4edda',
                  color: '#155724',
                  borderRadius: '4px',
                  fontSize: '0.9em'
                }}>
                  ✅ File selected: <strong>{formData.cvFileName}</strong>
                  <button
                    type="button"
                    onClick={() => {
                      setFormData(prev => ({
                        ...prev,
                        cvFile: null,
                        cvFileName: ''
                      }));
                      const fileInput = document.getElementById('cvInput');
                      if (fileInput) fileInput.value = '';
                    }}
                    style={{
                      marginLeft: '10px',
                      padding: '4px 10px',
                      background: 'none',
                      border: 'none',
                      color: '#155724',
                      cursor: 'pointer',
                      textDecoration: 'underline'
                    }}
                  >
                    Remove
                  </button>
                </div>
              )}
            </div>
            <small style={{ color: '#999', marginTop: '8px', display: 'block' }}>
              Your CV will be sent to the selected companies with your application.
            </small>
          </div>
        </section>

        {/* Company Selection Section */}
        <section style={{ marginBottom: '40px' }}>
          <h2 style={{
            fontSize: '1.5em',
            color: '#333',
            marginBottom: '20px',
            paddingBottom: '10px',
            borderBottom: '2px solid #f29b20'
          }}>
            🏢 Select Companies & Timeslots
          </h2>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(350px, 1fr))',
            gap: '20px',
            marginBottom: '30px'
          }}>
            {companies.map(company => (
              <div key={company._id} style={{
                background: '#f8f9fa',
                border: '2px solid #e0e0e0',
                borderRadius: '8px',
                padding: '20px',
                transition: 'all 0.3s'
              }}>
                <div style={{
                  display: 'flex',
                  alignItems: 'flex-start',
                  gap: '12px',
                  marginBottom: '15px'
                }}>
                  <input
                    type="checkbox"
                    id={`company-${company._id}`}
                    checked={selectedCompanies[company._id]?.selected || false}
                    onChange={(e) => {
                      if (e.target.checked) {
                        handleCompanySelect(company._id);
                      } else {
                        handleCompanyDeselect(company._id);
                      }
                    }}
                    style={{
                      width: '20px',
                      height: '20px',
                      marginTop: '2px',
                      cursor: 'pointer',
                      accentColor: '#f29b20'
                    }}
                  />
                  <label htmlFor={`company-${company._id}`} style={{ cursor: 'pointer', flex: 1 }}>
                    <h3 style={{ margin: 0, fontSize: '1.2em', color: '#333' }}>{company.name}</h3>
                  </label>
                </div>

                <div style={{ marginBottom: '15px', fontSize: '0.9em' }}>
                  <p style={{ margin: '5px 0', color: '#666' }}>
                    <strong>Industry:</strong> {company.industry}
                  </p>
                  <p style={{ margin: '5px 0', color: '#666' }}>
                    <strong>Package:</strong>{' '}
                    <span style={{
                      display: 'inline-block',
                      padding: '4px 12px',
                      borderRadius: '20px',
                      fontSize: '0.85em',
                      fontWeight: '600',
                      color: 'white',
                      backgroundColor: company.packageType === 'Platinum' ? '#c0c0c0' : 
                                      company.packageType === 'Gold' ? '#f29b20' : '#e8e8e8',
                      color: company.packageType === 'Gold' ? 'white' : '#333'
                    }}>
                      {company.packageType}
                    </span>
                  </p>
                  {company.description && (
                    <p style={{
                      color: '#888',
                      fontStyle: 'italic',
                      marginTop: '10px',
                      paddingTop: '10px',
                      borderTop: '1px solid #ddd'
                    }}>
                      {company.description}
                    </p>
                  )}
                </div>

                {/* Special Rules */}
                {selectedCompanies[company._id]?.selected && (
                  <div style={{ marginBottom: '15px' }}>
                    {/* ÖBB: German Requirement */}
                    {company.specialRules.germanRequired && (
                      <div style={{
                        background: '#fff3cd',
                        borderLeft: '4px solid #ff9800',
                        padding: '15px',
                        borderRadius: '4px',
                        marginBottom: '15px'
                      }}>
                        <strong style={{ display: 'block', marginBottom: '8px', color: '#333' }}>
                          ⚠️ German Requirement:
                        </strong>
                        <p style={{ margin: '8px 0', color: '#666', fontSize: '0.9em' }}>
                          This company requires B2 level German language skills.
                        </p>
                        <label style={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px',
                          cursor: 'pointer',
                          margin: '8px 0'
                        }}>
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.germanLanguageConfirmed || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'germanLanguageConfirmed', e.target.checked)}
                            style={{ cursor: 'pointer', accentColor: '#f29b20' }}
                          />
                          I confirm B2 level German language proficiency
                        </label>
                      </div>
                    )}

                    {/* Netconomy: Internship Interest */}
                    {company.specialRules.internshipVisa && (
                      <div style={{
                        background: '#d1ecf1',
                        borderLeft: '4px solid #17a2b8',
                        padding: '15px',
                        borderRadius: '4px',
                        marginBottom: '15px'
                      }}>
                        <strong style={{ display: 'block', marginBottom: '8px', color: '#333' }}>
                          📋 Internship Opportunity:
                        </strong>
                        <label style={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px',
                          cursor: 'pointer',
                          margin: '8px 0'
                        }}>
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.internshipInterest || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'internshipInterest', e.target.checked)}
                            style={{ cursor: 'pointer', accentColor: '#f29b20' }}
                          />
                          I am interested in an internship at this company
                        </label>
                        <label style={{
                          display: 'flex',
                          alignItems: 'center',
                          gap: '8px',
                          cursor: 'pointer',
                          margin: '8px 0'
                        }}>
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.hasValidVisa || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'hasValidVisa', e.target.checked)}
                            style={{ cursor: 'pointer', accentColor: '#f29b20' }}
                          />
                          I have a visa that allows for fulltime employment (38.5 hrs / week)
                        </label>
                        <p style={{ marginTop: '12px' }}>
                          <a href="https://drive.google.com/drive/folders/1FZnDU1Ps9H6PTsZA0MolcpL_I1twHovn?usp=sharing" target="_blank" rel="noopener noreferrer" style={{ color: '#f29b20', fontWeight: '600' }}>
                            Check out internship listings here →
                          </a>
                        </p>
                      </div>
                    )}

                    {/* Beyond Now: Limited Bookings Info */}
                    {company.specialRules.limitedBookings && (
                      <div style={{
                        background: '#d1ecf1',
                        borderLeft: '4px solid #17a2b8',
                        padding: '15px',
                        borderRadius: '4px',
                        marginBottom: '15px'
                      }}>
                        <strong style={{ display: 'block', marginBottom: '8px', color: '#333' }}>
                          ℹ️ Interview Format:
                        </strong>
                        <p style={{ margin: '8px 0', color: '#666', fontSize: '0.9em' }}>
                          2:1 interviews (one candidate with two HR representatives)
                        </p>
                      </div>
                    )}
                  </div>
                )}

                {/* Timeslot Selection */}
                {selectedCompanies[company._id]?.selected && availableSlots[company._id] && (
                  <div style={{ marginTop: '15px', paddingTop: '15px', borderTop: '1px solid #ddd' }}>
                    <label style={{ display: 'block', marginBottom: '10px', color: '#333', fontWeight: '600' }}>
                      Select a timeslot:
                    </label>
                    <div style={{
                      display: 'grid',
                      gridTemplateColumns: 'repeat(auto-fill, minmax(100px, 1fr))',
                      gap: '8px'
                    }}>
                      {availableSlots[company._id].length === 0 ? (
                        <p style={{ gridColumn: '1 / -1', textAlign: 'center', color: '#999', padding: '20px', fontStyle: 'italic' }}>
                          No available timeslots
                        </p>
                      ) : (
                        availableSlots[company._id].map((slot, index) => (
                          <button
                            key={index}
                            type="button"
                            onClick={() => slot.available && handleSlotSelect(company._id, slot.timeSlot)}
                            disabled={!slot.available}
                            style={{
                              padding: '10px',
                              border: `2px solid ${!slot.available ? '#ddd' : '#e0e0e0'}`,
                              background: selectedCompanies[company._id]?.selectedSlot === slot.timeSlot ? '#f29b20' : 'white',
                              borderRadius: '6px',
                              cursor: slot.available ? 'pointer' : 'not-allowed',
                              fontSize: '0.9em',
                              fontWeight: '500',
                              color: selectedCompanies[company._id]?.selectedSlot === slot.timeSlot ? 'white' : '#333',
                              transition: 'all 0.3s',
                              opacity: !slot.available ? 0.6 : 1
                            }}
                            title={!slot.available ? 'This timeslot is fully booked' : ''}
                          >
                            {slot.timeSlot}
                            {!slot.available && <span style={{ fontSize: '0.7em', display: 'block' }}>Booked</span>}
                          </button>
                        ))
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Submit Button */}
        <div style={{
          display: 'flex',
          justifyContent: 'center',
          gap: '15px',
          marginTop: '40px',
          paddingTop: '30px',
          borderTop: '2px solid #f0f0f0'
        }}>
          <button
            type="submit"
            disabled={submitting}
            style={{
              minWidth: '200px',
              display: 'inline-block',
              padding: '14px 40px',
              border: 'none',
              borderRadius: '6px',
              fontSize: '1.05em',
              fontWeight: '600',
              cursor: submitting ? 'not-allowed' : 'pointer',
              textTransform: 'uppercase',
              letterSpacing: '0.5px',
              background: submitting ? 'rgba(242, 155, 32, 0.6)' : 'linear-gradient(135deg, #f29b20 0%, #e68a1c 100%)',
              color: 'white',
              transition: 'all 0.3s',
              opacity: submitting ? 0.6 : 1
            }}
          >
            {submitting ? '⏳ Registering...' : '✅ Complete Registration'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CareerFairForm;
