import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './CareerFairForm.css';

const CareerFairForm = () => {
  // Form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    fieldOfStudy: '',
    motivation: '',
    internshipInterest: false,
    hasValidVisa: false,
    germanLanguageConfirmed: false
  });

  // UI State
  const [companies, setCompanies] = useState([]);
  const [selectedCompanies, setSelectedCompanies] = useState({});
  const [availableSlots, setAvailableSlots] = useState({});
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(false);
  const [submitting, setSubmitting] = useState(false);

  const fieldOfStudyOptions = [
    'Computer Science',
    'Software Engineering',
    'Data Science',
    'Cybersecurity',
    'Information Technology',
    'Business Administration',
    'Marketing',
    'Finance',
    'Mechanical Engineering',
    'Electrical Engineering',
    'Civil Engineering',
    'Biomedical Engineering',
    'Medicine',
    'Nursing',
    'Psychology',
    'Education',
    'Law',
    'Other'
  ];

  // Fetch companies on component mount
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        setLoading(true);
        const response = await axios.get('/api/career-fair/companies');
        setCompanies(response.data.data);
        setError(null);
      } catch (err) {
        setError('Failed to load companies. Please try again later.');
        console.error('Error fetching companies:', err);
      } finally {
        setLoading(false);
      }
    };

    fetchCompanies();
  }, []);

  // Fetch available slots when company is selected
  const handleCompanySelect = async (companyId) => {
    try {
      const response = await axios.get(`/api/career-fair/available-slots/${companyId}`);
      const company = response.data.data;

      setSelectedCompanies(prev => ({
        ...prev,
        [companyId]: {
          selected: true,
          company: company
        }
      }));

      setAvailableSlots(prev => ({
        ...prev,
        [companyId]: company.timeSlots
      }));

      setError(null);
    } catch (err) {
      if (err.response?.data?.maintenance) {
        setError(`${err.response.data.message}`);
      } else {
        setError('Failed to load available slots. Please try again.');
      }
      console.error('Error fetching slots:', err);
    }
  };

  // Handle company deselection
  const handleCompanyDeselect = (companyId) => {
    setSelectedCompanies(prev => {
      const updated = { ...prev };
      delete updated[companyId];
      return updated;
    });

    setAvailableSlots(prev => {
      const updated = { ...prev };
      delete updated[companyId];
      return updated;
    });
  };

  // Handle timeslot selection
  const handleSlotSelect = (companyId, timeSlot) => {
    setSelectedCompanies(prev => ({
      ...prev,
      [companyId]: {
        ...prev[companyId],
        selectedSlot: timeSlot
      }
    }));
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: type === 'checkbox' ? checked : value
    }));
  };

  // Handle special company field changes
  const handleCompanyFieldChange = (companyId, fieldName, value) => {
    setSelectedCompanies(prev => ({
      ...prev,
      [companyId]: {
        ...prev[companyId],
        [fieldName]: value
      }
    }));
  };

  // Validate form
  const validateForm = () => {
    if (!formData.firstName.trim()) {
      setError('First name is required');
      return false;
    }
    if (!formData.lastName.trim()) {
      setError('Last name is required');
      return false;
    }
    if (!formData.email.trim()) {
      setError('Email is required');
      return false;
    }
    if (!formData.phoneNumber.trim()) {
      setError('Phone number is required');
      return false;
    }
    if (!formData.fieldOfStudy) {
      setError('Field of study is required');
      return false;
    }
    if (!formData.motivation.trim()) {
      setError('Motivation is required');
      return false;
    }

    // Validate at least one company is selected
    const selectedCount = Object.values(selectedCompanies).filter(c => c.selected && c.selectedSlot).length;
    if (selectedCount === 0) {
      setError('Please select at least one company and timeslot');
      return false;
    }

    // Validate special rules
    for (const [companyId, company] of Object.entries(selectedCompanies)) {
      if (!company.selected) continue;

      // Check √ñBB German requirement
      if (company.company.specialRules.germanRequired && !company.germanLanguageConfirmed) {
        setError('√ñBB requires B2 level German. Please confirm.');
        return false;
      }

      // Check Netconomy internship requirement
      if (company.company.specialRules.internshipVisa && company.company.specialRules.internshipVisa) {
        // This is required for Netconomy if they selected it
        if (!company.internshipInterest || !company.hasValidVisa) {
          setError('Netconomy requires confirmation of internship interest and valid visa.');
          return false;
        }
      }
    }

    return true;
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError(null);

    if (!validateForm()) {
      return;
    }

    try {
      setSubmitting(true);

      // Build appointments array
      const appointments = Object.entries(selectedCompanies)
        .filter(([_, company]) => company.selected && company.selectedSlot)
        .map(([companyId, company]) => ({
          companyId,
          timeSlot: company.selectedSlot
        }));

      // Prepare submission data
      const submitData = {
        firstName: formData.firstName,
        lastName: formData.lastName,
        email: formData.email,
        phoneNumber: formData.phoneNumber,
        fieldOfStudy: formData.fieldOfStudy,
        motivation: formData.motivation,
        appointments,
        internshipInterest: formData.internshipInterest,
        hasValidVisa: formData.hasValidVisa,
        germanLanguageConfirmed: formData.germanLanguageConfirmed
      };

      const response = await axios.post('/api/career-fair/register', submitData);

      if (response.data.success) {
        setSuccess(true);
        setFormData({
          firstName: '',
          lastName: '',
          email: '',
          phoneNumber: '',
          fieldOfStudy: '',
          motivation: '',
          internshipInterest: false,
          hasValidVisa: false,
          germanLanguageConfirmed: false
        });
        setSelectedCompanies({});
        setAvailableSlots({});

        // Show success message for 5 seconds
        setTimeout(() => setSuccess(false), 5000);
      }
    } catch (err) {
      if (err.response?.data?.message) {
        setError(err.response.data.message);
      } else {
        setError('Failed to register. Please try again.');
      }
      console.error('Error submitting form:', err);
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="career-fair-container"><div className="spinner">Loading companies...</div></div>;
  }

  return (
    <div className="career-fair-container">
      <div className="career-fair-header">
        <h1>üéØ beWanted Career Fair Registration</h1>
        <p>Register for interviews with our partner companies</p>
      </div>

      <form onSubmit={handleSubmit} className="career-fair-form">
        {/* Error Message */}
        {error && <div className="alert alert-error">{error}</div>}
        {success && <div className="alert alert-success">‚úÖ Registration successful! Check your email for confirmation.</div>}

        {/* Student Information Section */}
        <section className="form-section">
          <h2>üìã Your Information</h2>
          
          <div className="form-row">
            <div className="form-group">
              <label htmlFor="firstName">First Name *</label>
              <input
                type="text"
                id="firstName"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
                maxLength="50"
                placeholder="Enter your first name"
              />
            </div>
            <div className="form-group">
              <label htmlFor="lastName">Last Name *</label>
              <input
                type="text"
                id="lastName"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
                maxLength="50"
                placeholder="Enter your last name"
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label htmlFor="email">Email *</label>
              <input
                type="email"
                id="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
                placeholder="your.email@example.com"
              />
            </div>
            <div className="form-group">
              <label htmlFor="phoneNumber">Phone Number *</label>
              <input
                type="tel"
                id="phoneNumber"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
                placeholder="+43 123 456789"
              />
            </div>
          </div>

          <div className="form-group full-width">
            <label htmlFor="fieldOfStudy">Field of Study *</label>
            <select
              id="fieldOfStudy"
              name="fieldOfStudy"
              value={formData.fieldOfStudy}
              onChange={handleInputChange}
              required
            >
              <option value="">Select your field of study</option>
              {fieldOfStudyOptions.map(field => (
                <option key={field} value={field}>{field}</option>
              ))}
            </select>
          </div>

          <div className="form-group full-width">
            <label htmlFor="motivation">Motivation / Cover Letter *</label>
            <textarea
              id="motivation"
              name="motivation"
              value={formData.motivation}
              onChange={handleInputChange}
              required
              maxLength="1000"
              rows="5"
              placeholder="Tell us why you're interested in the career fair and what you're looking for..."
            />
            <small>{formData.motivation.length}/1000 characters</small>
          </div>
        </section>

        {/* Company Selection Section */}
        <section className="form-section">
          <h2>üè¢ Select Companies & Timeslots</h2>
          
          <div className="companies-grid">
            {companies.map(company => (
              <div key={company._id} className="company-card">
                <div className="company-header">
                  <input
                    type="checkbox"
                    id={`company-${company._id}`}
                    checked={selectedCompanies[company._id]?.selected || false}
                    onChange={(e) => {
                      if (e.target.checked) {
                        handleCompanySelect(company._id);
                      } else {
                        handleCompanyDeselect(company._id);
                      }
                    }}
                  />
                  <label htmlFor={`company-${company._id}`}>
                    <h3>{company.name}</h3>
                  </label>
                </div>

                <div className="company-info">
                  <p><strong>Industry:</strong> {company.industry}</p>
                  <p><strong>Package:</strong> <span className={`badge badge-${company.packageType.toLowerCase()}`}>{company.packageType}</span></p>
                  {company.description && <p className="description">{company.description}</p>}
                </div>

                {/* Special Rules */}
                {selectedCompanies[company._id]?.selected && (
                  <div className="company-special-rules">
                    {/* √ñBB: German Requirement */}
                    {company.specialRules.germanRequired && (
                      <div className="special-rule warning">
                        <strong>‚ö†Ô∏è German Requirement:</strong>
                        <p>This company requires B2 level German language skills.</p>
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.germanLanguageConfirmed || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'germanLanguageConfirmed', e.target.checked)}
                          />
                          I confirm B2 level German language proficiency
                        </label>
                      </div>
                    )}

                    {/* Netconomy: Internship Interest */}
                    {company.specialRules.internshipVisa && (
                      <div className="special-rule info">
                        <strong>üìã Internship Opportunity:</strong>
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.internshipInterest || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'internshipInterest', e.target.checked)}
                          />
                          I am interested in an internship at this company
                        </label>
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={selectedCompanies[company._id]?.hasValidVisa || false}
                            onChange={(e) => handleCompanyFieldChange(company._id, 'hasValidVisa', e.target.checked)}
                          />
                          I have a visa that allows for fulltime employment (38.5 hrs / week)
                        </label>
                        <p className="internship-link">
                          <a href="https://drive.google.com/drive/folders/1FZnDU1Ps9H6PTsZA0MolcpL_I1twHovn?usp=sharing" target="_blank" rel="noopener noreferrer">
                            Check out internship listings here ‚Üí
                          </a>
                        </p>
                      </div>
                    )}

                    {/* Beyond Now: Limited Bookings Info */}
                    {company.specialRules.limitedBookings && (
                      <div className="special-rule info">
                        <strong>‚ÑπÔ∏è Interview Format:</strong>
                        <p>2:1 interviews (one candidate with two HR representatives)</p>
                      </div>
                    )}
                  </div>
                )}

                {/* Timeslot Selection */}
                {selectedCompanies[company._id]?.selected && availableSlots[company._id] && (
                  <div className="timeslot-selection">
                    <label><strong>Select a timeslot:</strong></label>
                    <div className="timeslot-options">
                      {availableSlots[company._id].length === 0 ? (
                        <p className="no-slots">No available timeslots</p>
                      ) : (
                        availableSlots[company._id].map((slot, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`timeslot-btn ${!slot.available ? 'disabled' : ''} ${selectedCompanies[company._id]?.selectedSlot === slot.timeSlot ? 'selected' : ''}`}
                            onClick={() => slot.available && handleSlotSelect(company._id, slot.timeSlot)}
                            disabled={!slot.available}
                            title={!slot.available ? 'This timeslot is fully booked' : ''}
                          >
                            {slot.timeSlot}
                            {!slot.available && <span className="booked-badge">Booked</span>}
                          </button>
                        ))
                      )}
                    </div>
                  </div>
                )}
              </div>
            ))}
          </div>
        </section>

        {/* Submit Button */}
        <div className="form-actions">
          <button
            type="submit"
            className="btn btn-primary"
            disabled={submitting}
          >
            {submitting ? '‚è≥ Registering...' : '‚úÖ Complete Registration'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CareerFairForm;
