import React, { useState, useEffect } from 'react';
import axios from 'axios';
import '../styles/CareerFairForm.css';

const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const CareerFairForm = () => {
  // Form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    fieldOfStudy: '',
    motivation: '',
    cv: null
  });

  const [selectedAppointments, setSelectedAppointments] = useState({});
  const [specialCheckboxes, setSpecialCheckboxes] = useState({});
  const [companies, setCompanies] = useState([]);
  const [availableSlots, setAvailableSlots] = useState({});
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  // Fetch companies on mount
  useEffect(() => {
    fetchCompanies();
  }, []);

  const fetchCompanies = async () => {
    try {
      setLoading(true);
      const response = await axios.get(`${API_URL}/career-fair/companies`);
      setCompanies(response.data.data);
      setError('');
    } catch (err) {
      console.error('Error fetching companies:', err);
      setError('Failed to load companies');
    } finally {
      setLoading(false);
    }
  };

  const fetchAvailableSlots = async (companyId) => {
    try {
      const response = await axios.get(
        `${API_URL}/career-fair/available-slots/${companyId}`,
        {
          params: { email: formData.email }
        }
      );
      setAvailableSlots(prev => ({
        ...prev,
        [companyId]: response.data.data.timeSlots
      }));
    } catch (err) {
      console.error('Error fetching available slots:', err);
      if (err.response?.data?.maintenance) {
        setError(`${err.response.data.message}`);
      }
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCVChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      if (file.size > 2 * 1024 * 1024) {
        setError('CV file must be less than 2MB');
        return;
      }
      const allowedTypes = ['application/pdf', 'application/msword', 'application/vnd.openxmlformats-officedocument.wordprocessingml.document'];
      if (!allowedTypes.includes(file.type)) {
        setError('Only PDF, DOC, and DOCX files are allowed');
        return;
      }
      setFormData(prev => ({
        ...prev,
        cv: file
      }));
      setError('');
    }
  };

  const handleCompanySelect = (company) => {
    setSelectedAppointments(prev => {
      const newSelected = { ...prev };
      
      if (newSelected[company._id]) {
        delete newSelected[company._id];
        delete specialCheckboxes[company._id];
      } else {
        newSelected[company._id] = null;
        // Initialize special checkboxes for this company
        if (company.specialRules?.internshipVisa) {
          setSpecialCheckboxes(prev => ({
            ...prev,
            [company._id]: { internshipInterest: false, hasValidVisa: false }
          }));
        }
        if (company.specialRules?.germanRequired) {
          setSpecialCheckboxes(prev => ({
            ...prev,
            [company._id]: { germanLanguageConfirmed: false }
          }));
        }
      }
      
      return newSelected;
    });

    // Fetch slots if not already fetched
    if (!availableSlots[company._id]) {
      fetchAvailableSlots(company._id);
    }
  };

  const handleTimeSlotSelect = (companyId, timeSlot) => {
    setSelectedAppointments(prev => ({
      ...prev,
      [companyId]: timeSlot
    }));
  };

  const handleSpecialCheckboxChange = (companyId, field, value) => {
    setSpecialCheckboxes(prev => ({
      ...prev,
      [companyId]: {
        ...prev[companyId],
        [field]: value
      }
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validate
    if (!formData.firstName || !formData.lastName || !formData.email || !formData.phoneNumber || !formData.fieldOfStudy || !formData.motivation) {
      setError('Please fill in all required fields');
      return;
    }

    // Validate appointments
    const appointmentArray = Object.entries(selectedAppointments)
      .filter(([_, timeSlot]) => timeSlot)
      .map(([companyId, timeSlot]) => ({ companyId, timeSlot }));

    if (appointmentArray.length === 0) {
      setError('Please select at least one appointment');
      return;
    }

    // Validate special requirements
    for (const [companyId, apt] of Object.entries(selectedAppointments)) {
      if (!apt) continue;
      
      const company = companies.find(c => c._id === companyId);
      if (company?.specialRules?.germanRequired) {
        if (!specialCheckboxes[companyId]?.germanLanguageConfirmed) {
          setError(`${company.name} requires confirmation of B2 level German`);
          return;
        }
      }
      if (company?.specialRules?.internshipVisa) {
        if (!specialCheckboxes[companyId]?.internshipInterest) {
          setError(`Please confirm your internship interest for ${company.name}`);
          return;
        }
      }
    }

    try {
      setSubmitting(true);
      setError('');

      const formDataToSubmit = new FormData();
      formDataToSubmit.append('firstName', formData.firstName);
      formDataToSubmit.append('lastName', formData.lastName);
      formDataToSubmit.append('email', formData.email);
      formDataToSubmit.append('phoneNumber', formData.phoneNumber);
      formDataToSubmit.append('fieldOfStudy', formData.fieldOfStudy);
      formDataToSubmit.append('motivation', formData.motivation);
      
      if (formData.cv) {
        formDataToSubmit.append('cv', formData.cv);
      }

      formDataToSubmit.append('appointments', JSON.stringify(appointmentArray));

      // Add special checkboxes
      for (const [companyId, checkboxes] of Object.entries(specialCheckboxes)) {
        if (checkboxes) {
          Object.entries(checkboxes).forEach(([key, value]) => {
            formDataToSubmit.append(`${key}`, value);
          });
        }
      }

      const response = await axios.post(
        `${API_URL}/career-fair/register`,
        formDataToSubmit,
        {
          headers: { 'Content-Type': 'multipart/form-data' }
        }
      );

      setSuccess(`Registration successful! ${response.data.data.appointmentsCreated} appointment(s) confirmed.`);
      setFormData({
        firstName: '',
        lastName: '',
        email: '',
        phoneNumber: '',
        fieldOfStudy: '',
        motivation: '',
        cv: null
      });
      setSelectedAppointments({});
      setSpecialCheckboxes({});

      // Reset file input
      const cvInput = document.querySelector('input[type="file"]');
      if (cvInput) cvInput.value = '';

      setTimeout(() => setSuccess(''), 5000);
    } catch (err) {
      console.error('Error submitting form:', err);
      setError(err.response?.data?.message || 'Error submitting registration');
    } finally {
      setSubmitting(false);
    }
  };

  if (loading) {
    return <div className="spinner">Loading career fair information...</div>;
  }

  return (
    <div className="career-fair-container">
      <div className="career-fair-header">
        <h1>ðŸŽ¯ beWanted Career Fair</h1>
        <p>November 26, 2025 | Register for interviews with top companies</p>
      </div>

      <form className="career-fair-form" onSubmit={handleSubmit}>
        {error && <div className="alert alert-error">{error}</div>}
        {success && <div className="alert alert-success">{success}</div>}

        {/* Student Information Section */}
        <div className="form-section">
          <h2>ðŸ‘¤ Your Information</h2>
          
          <div className="form-row">
            <div className="form-group">
              <label>First Name *</label>
              <input
                type="text"
                name="firstName"
                value={formData.firstName}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Last Name *</label>
              <input
                type="text"
                name="lastName"
                value={formData.lastName}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Email *</label>
              <input
                type="email"
                name="email"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>
            <div className="form-group">
              <label>Phone Number *</label>
              <input
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleInputChange}
                required
              />
            </div>
          </div>

          <div className="form-row">
            <div className="form-group">
              <label>Field of Study *</label>
              <select
                name="fieldOfStudy"
                value={formData.fieldOfStudy}
                onChange={handleInputChange}
                required
              >
                <option value="">Select your field of study</option>
                <option value="Computer Science">Computer Science</option>
                <option value="Software Engineering">Software Engineering</option>
                <option value="Data Science">Data Science</option>
                <option value="Cybersecurity">Cybersecurity</option>
                <option value="Information Technology">Information Technology</option>
                <option value="Business Administration">Business Administration</option>
                <option value="Marketing">Marketing</option>
                <option value="Finance">Finance</option>
                <option value="Mechanical Engineering">Mechanical Engineering</option>
                <option value="Electrical Engineering">Electrical Engineering</option>
                <option value="Civil Engineering">Civil Engineering</option>
                <option value="Biomedical Engineering">Biomedical Engineering</option>
                <option value="Medicine">Medicine</option>
                <option value="Nursing">Nursing</option>
                <option value="Psychology">Psychology</option>
                <option value="Education">Education</option>
                <option value="Law">Law</option>
                <option value="Other">Other</option>
              </select>
            </div>
          </div>

          <div className="form-row full">
            <div className="form-group">
              <label>Motivation *</label>
              <textarea
                name="motivation"
                value={formData.motivation}
                onChange={handleInputChange}
                placeholder="Tell us why you want to attend this career fair..."
                required
              />
            </div>
          </div>

          <div className="form-row full">
            <div className="form-group">
              <label>Upload CV (PDF, DOC, DOCX - Max 2MB)</label>
              <input
                type="file"
                onChange={handleCVChange}
                accept=".pdf,.doc,.docx"
              />
              {formData.cv && <small>âœ… File selected: {formData.cv.name}</small>}
            </div>
          </div>
        </div>

        {/* Company Selection Section */}
        <div className="form-section">
          <h2>ðŸ¢ Select Companies</h2>
          
          <div className="companies-grid">
            {companies.map(company => (
              <div
                key={company._id}
                className={`company-card ${selectedAppointments[company._id] ? 'selected' : ''}`}
              >
                <div className="company-header">
                  <input
                    type="checkbox"
                    id={`company-${company._id}`}
                    checked={!!selectedAppointments[company._id]}
                    onChange={() => handleCompanySelect(company)}
                    disabled={!company.bookingAvailable}
                  />
                  <label htmlFor={`company-${company._id}`}>
                    <h3>{company.name}</h3>
                  </label>
                </div>

                <div className="company-info">
                  <p><strong>ðŸ“Š Package:</strong> <span className={`badge badge-${company.packageType.toLowerCase()}`}>{company.packageType}</span></p>
                  <p><strong>ðŸ­ Industry:</strong> {company.industry}</p>
                  <p><strong>ðŸ’¼ Positions:</strong> {company.positions.join(', ')}</p>
                  <p className="description">{company.description}</p>
                </div>

                {/* Special Rules */}
                {(company.specialRules?.germanRequired || company.specialRules?.internshipVisa) && selectedAppointments[company._id] && (
                  <div className="company-special-rules">
                    {company.specialRules?.germanRequired && (
                      <div className="special-rule warning">
                        <strong>âš ï¸ German Language Requirement</strong>
                        <p>This company requires B2 level German proficiency for the position.</p>
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={specialCheckboxes[company._id]?.germanLanguageConfirmed || false}
                            onChange={(e) => handleSpecialCheckboxChange(company._id, 'germanLanguageConfirmed', e.target.checked)}
                          />
                          I confirm B2 level German proficiency
                        </label>
                      </div>
                    )}

                    {company.specialRules?.internshipVisa && (
                      <div className="special-rule info">
                        <strong>ðŸŽ“ Internship Opportunity</strong>
                        <p>Are you interested in an internship at this company?</p>
                        <label className="checkbox-label">
                          <input
                            type="checkbox"
                            checked={specialCheckboxes[company._id]?.internshipInterest || false}
                            onChange={(e) => handleSpecialCheckboxChange(company._id, 'internshipInterest', e.target.checked)}
                          />
                          I am interested in an internship and I have a visa that allows for fulltime employment (38.5 hrs/week)
                        </label>
                        <div className="internship-link">
                          <a href="https://drive.google.com/drive/folders/1FZnDU1Ps9H6PTsZA0MolcpL_I1twHovn?usp=sharing" target="_blank" rel="noopener noreferrer">
                            Check out internship listings here
                          </a>
                        </div>
                      </div>
                    )}
                  </div>
                )}

                {/* Timeslot Selection */}
                {selectedAppointments[company._id] !== null && availableSlots[company._id] && (
                  <div className="timeslot-selection">
                    <label>Select a time slot:</label>
                    <div className="timeslot-options">
                      {availableSlots[company._id].length > 0 ? (
                        availableSlots[company._id].map((slot, index) => (
                          <button
                            key={index}
                            type="button"
                            className={`timeslot-btn ${selectedAppointments[company._id] === slot.timeSlot ? 'selected' : ''} ${!slot.available ? 'disabled' : ''} ${slot.conflict ? 'disabled' : ''}`}
                            onClick={() => handleTimeSlotSelect(company._id, slot.timeSlot)}
                            disabled={!slot.available}
                            title={slot.conflict ? 'You already have an appointment at this time' : slot.available ? slot.timeSlot : 'Fully booked'}
                          >
                            {slot.timeSlot}
                            {!slot.available && <span className="booked-badge">Booked</span>}
                            {slot.conflict && <span className="booked-badge">Conflict</span>}
                          </button>
                        ))
                      ) : (
                        <div className="no-slots">No available slots</div>
                      )}
                    </div>
                  </div>
                )}

                {!company.bookingAvailable && (
                  <div style={{ padding: '10px', background: '#fff3cd', borderRadius: '4px', marginTop: '10px', color: '#856404' }}>
                    <strong>â³ Booking Not Available</strong>
                    <p>This company's bookings are not available at the moment. Please check back later.</p>
                  </div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* Submit Button */}
        <div className="form-actions">
          <button type="submit" className="btn btn-primary" disabled={submitting}>
            {submitting ? 'Registering...' : 'âœ… Register for Career Fair'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default CareerFairForm;
