import React, { useState, useEffect } from 'react';

const CareerFairForm = () => {
  // API helper functions
  const apiCall = async (endpoint, options = {}) => {
    const baseUrl = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';
    const url = `${baseUrl}${endpoint}`;
    
    const defaultOptions = {
      headers: {
        'Content-Type': 'application/json',
      },
    };

    const config = { ...defaultOptions, ...options };

    try {
      const response = await fetch(url, config);
      const data = await response.json();
      
      if (!response.ok) {
        throw new Error(data.message || `HTTP error! status: ${response.status}`);
      }
      
      return data;
    } catch (error) {
      console.error('API call failed:', error);
      throw error;
    }
  };

  // Form state
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    phoneNumber: '',
    fieldOfStudy: '',
    motivation: ''
  });

  // Available data from backend
  const [fieldsOfStudy] = useState([
    'Computer Science', 'Software Engineering', 'Data Science', 'Cybersecurity',
    'Information Technology', 'Business Administration', 'Marketing', 'Finance',
    'Mechanical Engineering', 'Electrical Engineering', 'Civil Engineering',
    'Biomedical Engineering', 'Medicine', 'Nursing', 'Psychology', 'Education',
    'Law', 'Other'
  ]);

  const [companies, setCompanies] = useState([]);
  const [availability, setAvailability] = useState({});
  const [timeSlots, setTimeSlots] = useState([]);
  
  // Booking state
  const [selectedCompanies, setSelectedCompanies] = useState([]);
  const [bookedSlots, setBookedSlots] = useState({});
  const [usedTimeSlots, setUsedTimeSlots] = useState(new Set());
  const [currentStep, setCurrentStep] = useState(1);
  const [errors, setErrors] = useState({});
  const [selectedDate, setSelectedDate] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitStatus, setSubmitStatus] = useState(null);

  // Generate available dates (next 30 days, excluding weekends)
  const generateAvailableDates = () => {
    const dates = [];
    const today = new Date();
    let currentDate = new Date(today);
    
    while (dates.length < 20) {
      currentDate.setDate(currentDate.getDate() + 1);
      const dayOfWeek = currentDate.getDay();
      
      if (dayOfWeek !== 0 && dayOfWeek !== 6) {
        dates.push(new Date(currentDate).toISOString().split('T')[0]);
      }
    }
    
    return dates;
  };

  const [availableDates] = useState(generateAvailableDates());

  // Fetch companies from backend
  useEffect(() => {
    const fetchCompanies = async () => {
      try {
        const response = await apiCall('/career-fair/companies');
        if (response.success) {
          setCompanies(response.data);
        }
      } catch (error) {
        console.error('Error fetching companies:', error);
        setErrors({ general: 'Failed to load companies. Please refresh the page.' });
      }
    };

    fetchCompanies();
  }, []);

  // Fetch availability when date changes
  useEffect(() => {
    const fetchAvailability = async () => {
      if (!selectedDate) return;

      try {
        setLoading(true);
        const response = await apiCall(`/career-fair/available-slots/${selectedDate}`);
        if (response.success) {
          setAvailability(response.data.availability);
          setTimeSlots(response.data.timeSlots);
          
          // Reset selections when date changes
          setBookedSlots({});
          setUsedTimeSlots(new Set());
        }
      } catch (error) {
        console.error('Error fetching availability:', error);
        setErrors({ general: 'Failed to load available time slots.' });
      } finally {
        setLoading(false);
      }
    };

    fetchAvailability();
  }, [selectedDate]);

  // Validation
  const validateForm = () => {
    const newErrors = {};
    
    if (!formData.firstName.trim()) newErrors.firstName = 'First name is required';
    if (!formData.lastName.trim()) newErrors.lastName = 'Last name is required';
    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Invalid email format';
    }
    if (!formData.phoneNumber.trim()) {
      newErrors.phoneNumber = 'Phone number is required';
    } else if (!/^\+?[\d\s\-\(\)]{10,}$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Invalid phone number format';
    }
    if (!formData.fieldOfStudy) newErrors.fieldOfStudy = 'Field of study is required';
    if (!formData.motivation.trim()) newErrors.motivation = 'Motivation is required';

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  // Handle form input changes
  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  // Handle company selection
  const handleCompanySelect = (company) => {
    setSelectedCompanies(prev => {
      const isSelected = prev.find(c => c._id === company._id);
      if (isSelected) {
        // Remove company and its booking
        const newBookedSlots = { ...bookedSlots };
        if (newBookedSlots[company._id]) {
          const timeSlot = newBookedSlots[company._id][selectedDate];
          if (timeSlot) {
            setUsedTimeSlots(prev => {
              const newSet = new Set(prev);
              newSet.delete(timeSlot);
              return newSet;
            });
          }
          delete newBookedSlots[company._id];
          setBookedSlots(newBookedSlots);
        }
        return prev.filter(c => c._id !== company._id);
      } else {
        return [...prev, company];
      }
    });
  };

  // Handle time slot booking
  const handleTimeSlotSelect = (companyId, timeSlot) => {
    // Check if this time slot is available for this company
    if (!availability[companyId] || !availability[companyId][timeSlot]) {
      alert('This time slot is not available for this company.');
      return;
    }

    // Check if this time slot is already used by student
    if (usedTimeSlots.has(timeSlot)) {
      alert('You already have an appointment at this time. Please select a different time.');
      return;
    }

    // Update booked slots
    setBookedSlots(prev => {
      const newBookedSlots = { ...prev };
      
      // Remove old time slot from used slots if company had a previous booking
      if (newBookedSlots[companyId] && newBookedSlots[companyId][selectedDate]) {
        const oldTimeSlot = newBookedSlots[companyId][selectedDate];
        setUsedTimeSlots(prevUsed => {
          const newSet = new Set(prevUsed);
          newSet.delete(oldTimeSlot);
          return newSet;
        });
      }
      
      // Add new booking
      if (!newBookedSlots[companyId]) {
        newBookedSlots[companyId] = {};
      }
      newBookedSlots[companyId][selectedDate] = timeSlot;
      
      return newBookedSlots;
    });

    // Add new time slot to used slots
    setUsedTimeSlots(prev => new Set([...prev, timeSlot]));
  };

  // Handle form submission
  const handleSubmit = (e) => {
    e.preventDefault();
    if (validateForm()) {
      setCurrentStep(2);
    }
  };

  // Handle final booking submission
  const handleBookingSubmit = async () => {
    setLoading(true);
    setSubmitStatus(null);

    try {
      // Prepare booking data
      const appointments = Object.entries(bookedSlots).map(([companyId, dates]) => ({
        companyId,
        companyName: companies.find(c => c._id === companyId)?.name,
        appointments: Object.entries(dates).map(([date, timeSlot]) => ({
          date,
          timeSlot
        }))
      }));

      const bookingData = {
        studentInfo: formData,
        appointments
      };

      const response = await apiCall('/career-fair/register', {
        method: 'POST',
        body: JSON.stringify(bookingData)
      });

      if (response.success) {
        setSubmitStatus({
          type: 'success',
          message: response.message,
          data: response.data
        });

        // Reset form after successful submission
        setTimeout(() => {
          setFormData({
            firstName: '',
            lastName: '',
            email: '',
            phoneNumber: '',
            fieldOfStudy: '',
            motivation: ''
          });
          setSelectedCompanies([]);
          setBookedSlots({});
          setUsedTimeSlots(new Set());
          setCurrentStep(1);
          setSelectedDate('');
          setSubmitStatus(null);
        }, 5000);
      }
    } catch (error) {
      console.error('Booking submission error:', error);
      setSubmitStatus({
        type: 'error',
        message: error.message || 'Failed to submit booking. Please try again.',
        errors: error.errors
      });
    } finally {
      setLoading(false);
    }
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  // Styles matching your CSS theme
  const styles = {
    container: {
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
      fontFamily: '-apple-system, BlinkMacSystemFont, "Segoe UI", "Roboto", sans-serif',
      background: 'linear-gradient(135deg, #000000 0%, #1a1a1a 100%)',
      minHeight: '100vh',
      color: '#e0e0e0'
    },
    card: {
      background: '#1a1a1a',
      border: '1px solid #333333',
      padding: '40px',
      borderRadius: '12px',
      boxShadow: '0 10px 40px rgba(0, 0, 0, 0.3)',
      marginBottom: '30px'
    },
    title: {
      textAlign: 'center',
      marginBottom: '10px',
      color: '#f29b20',
      fontSize: '2.5rem',
      fontWeight: '700',
      textShadow: '0 0 10px rgba(242, 155, 32, 0.3)'
    },
    subtitle: {
      textAlign: 'center',
      marginBottom: '30px',
      color: '#cccccc',
      fontSize: '1.1rem'
    },
    formGroup: {
      marginBottom: '20px'
    },
    label: {
      display: 'block',
      marginBottom: '8px',
      fontWeight: '500',
      color: 'white'
    },
    input: {
      width: '100%',
      padding: '12px',
      border: '2px solid #333333',
      borderRadius: '8px',
      fontSize: '1rem',
      transition: 'border-color 0.3s ease',
      backgroundColor: '#1a1a1a',
      color: 'white'
    },
    inputError: {
      borderColor: '#ff6b6b'
    },
    inputFocus: {
      borderColor: '#f29b20',
      boxShadow: '0 0 10px rgba(242, 155, 32, 0.2)'
    },
    error: {
      color: '#ff6b6b',
      fontSize: '0.875rem',
      marginTop: '4px',
      display: 'block'
    },
    button: {
      padding: '16px 32px',
      backgroundColor: '#f29b20',
      color: '#000000',
      border: '2px solid #f29b20',
      borderRadius: '8px',
      fontSize: '1.1rem',
      fontWeight: '600',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      textTransform: 'uppercase',
      letterSpacing: '0.5px'
    },
    buttonSecondary: {
      backgroundColor: 'transparent',
      color: '#f29b20',
      border: '2px solid #f29b20'
    },
    buttonDisabled: {
      opacity: 0.6,
      cursor: 'not-allowed'
    },
    progressStep: {
      width: '40px',
      height: '40px',
      borderRadius: '50%',
      display: 'flex',
      alignItems: 'center',
      justifyContent: 'center',
      fontWeight: 'bold',
      color: 'white'
    },
    progressLine: {
      width: '100px',
      height: '2px',
      margin: '0 10px'
    },
    companyCard: {
      padding: '20px',
      border: '2px solid #333333',
      borderRadius: '12px',
      cursor: 'pointer',
      transition: 'all 0.3s ease',
      background: '#000000'
    },
    companyCardSelected: {
      borderColor: '#f29b20',
      backgroundColor: 'rgba(242, 155, 32, 0.1)',
      boxShadow: '0 4px 20px rgba(242, 155, 32, 0.2)'
    },
    timeSlotButton: {
      padding: '8px 4px',
      borderRadius: '6px',
      fontSize: '0.8rem',
      transition: 'all 0.3s ease',
      border: '2px solid',
      cursor: 'pointer'
    },
    errorMessage: {
      backgroundColor: 'rgba(255, 107, 107, 0.1)',
      color: '#ff6b6b',
      border: '1px solid #ff6b6b',
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px'
    },
    successMessage: {
      backgroundColor: 'rgba(74, 222, 128, 0.1)',
      color: '#4ade80',
      border: '1px solid #4ade80',
      padding: '15px',
      borderRadius: '6px',
      marginBottom: '20px'
    },
    instructionsPanel: {
      marginTop: '30px',
      padding: '20px',
      backgroundColor: '#1a1a1a',
      borderRadius: '12px',
      border: '1px solid #333333'
    }
  };

  // Success screen
  if (submitStatus?.type === 'success') {
    return (
      <div style={styles.container}>
        <div style={{...styles.card, textAlign: 'center'}}>
          <div style={{ fontSize: '4rem', marginBottom: '20px' }}>🎉</div>
          <h1 style={{ color: '#4ade80', marginBottom: '20px', fontSize: '2.5rem' }}>Registration Successful!</h1>
          <p style={{ fontSize: '1.1rem', marginBottom: '30px', color: '#cccccc' }}>
            {submitStatus.message}
          </p>
          
          <div style={{
            backgroundColor: '#000000',
            padding: '20px',
            borderRadius: '8px',
            marginBottom: '30px',
            textAlign: 'left',
            border: '1px solid #333333'
          }}>
            <h3 style={{color: '#f29b20', marginBottom: '15px'}}>Your Appointments:</h3>
            {submitStatus.data?.appointments?.map((apt, index) => (
              <div key={index} style={{ 
                marginBottom: '10px', 
                padding: '10px',
                backgroundColor: '#1a1a1a',
                borderRadius: '4px',
                border: '1px solid #333333',
                color: 'white'
              }}>
                <strong style={{color: '#f29b20'}}>{apt.company.name}</strong><br />
                📅 {formatDate(apt.date)} at {apt.timeSlot}
              </div>
            ))}
          </div>

          <p style={{ color: '#cccccc', fontSize: '0.9rem' }}>
            You will receive a confirmation email shortly. This page will redirect automatically in a few seconds.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* Progress Indicator */}
      <div style={{ marginBottom: '30px' }}>
        <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'center', marginBottom: '20px' }}>
          <div style={{ 
            ...styles.progressStep,
            backgroundColor: currentStep >= 1 ? '#f29b20' : '#333333'
          }}>1</div>
          <div style={{ 
            ...styles.progressLine,
            backgroundColor: currentStep >= 2 ? '#f29b20' : '#333333'
          }}></div>
          <div style={{ 
            ...styles.progressStep,
            backgroundColor: currentStep >= 2 ? '#f29b20' : '#333333'
          }}>2</div>
        </div>
        <div style={{ display: 'flex', justifyContent: 'center', gap: '80px' }}>
          <span style={{ color: currentStep >= 1 ? '#f29b20' : '#cccccc', fontWeight: '500' }}>
            Student Information
          </span>
          <span style={{ color: currentStep >= 2 ? '#f29b20' : '#cccccc', fontWeight: '500' }}>
            Company Appointments
          </span>
        </div>
      </div>

      {/* Error Display */}
      {errors.general && (
        <div style={styles.errorMessage}>
          {errors.general}
        </div>
      )}

      {submitStatus?.type === 'error' && (
        <div style={styles.errorMessage}>
          <strong>Error:</strong> {submitStatus.message}
          {submitStatus.errors && (
            <ul style={{ marginTop: '10px', marginBottom: '0' }}>
              {submitStatus.errors.map((error, index) => (
                <li key={index}>{error}</li>
              ))}
            </ul>
          )}
        </div>
      )}

      {/* Step 1: Student Information Form */}
      {currentStep === 1 && (
        <div style={styles.card}>
          <h1 style={styles.title}>Career Fair Registration</h1>
          <p style={styles.subtitle}>
            Join our career fair and connect with top companies
          </p>

          <form onSubmit={handleSubmit}>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
              <div style={styles.formGroup}>
                <label style={styles.label}>First Name *</label>
                <input
                  type="text"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  style={{
                    ...styles.input,
                    ...(errors.firstName && styles.inputError)
                  }}
                  onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                  onBlur={(e) => e.target.style.borderColor = errors.firstName ? '#ff6b6b' : '#333333'}
                />
                {errors.firstName && (
                  <span style={styles.error}>{errors.firstName}</span>
                )}
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Last Name *</label>
                <input
                  type="text"
                  name="lastName"
                  value={formData.lastName}
                  onChange={handleInputChange}
                  style={{
                    ...styles.input,
                    ...(errors.lastName && styles.inputError)
                  }}
                  onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                  onBlur={(e) => e.target.style.borderColor = errors.lastName ? '#ff6b6b' : '#333333'}
                />
                {errors.lastName && (
                  <span style={styles.error}>{errors.lastName}</span>
                )}
              </div>
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: '20px', marginBottom: '20px' }}>
              <div style={styles.formGroup}>
                <label style={styles.label}>Email Address *</label>
                <input
                  type="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  style={{
                    ...styles.input,
                    ...(errors.email && styles.inputError)
                  }}
                  onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                  onBlur={(e) => e.target.style.borderColor = errors.email ? '#ff6b6b' : '#333333'}
                />
                {errors.email && (
                  <span style={styles.error}>{errors.email}</span>
                )}
              </div>

              <div style={styles.formGroup}>
                <label style={styles.label}>Phone Number *</label>
                <input
                  type="tel"
                  name="phoneNumber"
                  value={formData.phoneNumber}
                  onChange={handleInputChange}
                  placeholder="+1 (555) 123-4567"
                  style={{
                    ...styles.input,
                    ...(errors.phoneNumber && styles.inputError)
                  }}
                  onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                  onBlur={(e) => e.target.style.borderColor = errors.phoneNumber ? '#ff6b6b' : '#333333'}
                />
                {errors.phoneNumber && (
                  <span style={styles.error}>{errors.phoneNumber}</span>
                )}
              </div>
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Field of Study *</label>
              <select
                name="fieldOfStudy"
                value={formData.fieldOfStudy}
                onChange={handleInputChange}
                style={{
                  ...styles.input,
                  ...(errors.fieldOfStudy && styles.inputError)
                }}
                onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                onBlur={(e) => e.target.style.borderColor = errors.fieldOfStudy ? '#ff6b6b' : '#333333'}
              >
                <option value="" style={{backgroundColor: '#1a1a1a', color: '#888888'}}>Select your field of study</option>
                {fieldsOfStudy.map((field, index) => (
                  <option key={index} value={field} style={{backgroundColor: '#1a1a1a', color: 'white'}}>
                    {field}
                  </option>
                ))}
              </select>
              {errors.fieldOfStudy && (
                <span style={styles.error}>{errors.fieldOfStudy}</span>
              )}
            </div>

            <div style={styles.formGroup}>
              <label style={styles.label}>Why do you want to join our career fair? *</label>
              <textarea
                name="motivation"
                value={formData.motivation}
                onChange={handleInputChange}
                rows="4"
                placeholder="Tell us about your career goals and what you hope to achieve..."
                style={{
                  ...styles.input,
                  fontFamily: 'inherit',
                  resize: 'vertical',
                  ...(errors.motivation && styles.inputError)
                }}
                onFocus={(e) => Object.assign(e.target.style, styles.inputFocus)}
                onBlur={(e) => e.target.style.borderColor = errors.motivation ? '#ff6b6b' : '#333333'}
              />
              {errors.motivation && (
                <span style={styles.error}>{errors.motivation}</span>
              )}
            </div>

            <button
              type="submit"
              style={{...styles.button, width: '100%'}}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#ffb347';
                e.target.style.transform = 'translateY(-2px)';
                e.target.style.boxShadow = '0 4px 15px rgba(242, 155, 32, 0.3)';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = '#f29b20';
                e.target.style.transform = 'translateY(0)';
                e.target.style.boxShadow = 'none';
              }}
            >
              Continue to Company Selection
            </button>
          </form>
        </div>
      )}

      {/* Step 2: Company Selection and Appointment Booking */}
      {currentStep === 2 && (
        <div style={styles.card}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '30px' }}>
            <div>
              <h2 style={{ margin: '0 0 10px 0', color: '#f29b20', fontSize: '2rem' }}>
                Select Companies & Book Appointments
              </h2>
              <p style={{ margin: 0, color: '#cccccc' }}>
                Choose companies you're interested in and schedule your appointments
              </p>
            </div>
            <button
              onClick={() => setCurrentStep(1)}
              style={{...styles.button, ...styles.buttonSecondary}}
              onMouseOver={(e) => {
                e.target.style.backgroundColor = '#f29b20';
                e.target.style.color = '#000000';
                e.target.style.transform = 'translateY(-2px)';
              }}
              onMouseOut={(e) => {
                e.target.style.backgroundColor = 'transparent';
                e.target.style.color = '#f29b20';
                e.target.style.transform = 'translateY(0)';
              }}
            >
              Back to Form
            </button>
          </div>

          {/* Date Selection */}
          <div style={{...styles.formGroup, marginBottom: '30px'}}>
            <label style={styles.label}>Select Date *</label>
            <select
              value={selectedDate}
              onChange={(e) => setSelectedDate(e.target.value)}
              style={{
                ...styles.input,
                minWidth: '300px',
                width: 'auto'
              }}
            >
              <option value="" style={{backgroundColor: '#1a1a1a', color: '#888888'}}>Choose a date</option>
              {availableDates.map(date => (
                <option key={date} value={date} style={{backgroundColor: '#1a1a1a', color: 'white'}}>
                  {formatDate(date)}
                </option>
              ))}
            </select>
          </div>

          {selectedDate && (
            <>
              {/* Loading Indicator */}
              {loading && (
                <div style={{ textAlign: 'center', padding: '20px', color: '#cccccc' }}>
                  <div style={{
                    width: '40px',
                    height: '40px',
                    border: '4px solid #333333',
                    borderTop: '4px solid #f29b20',
                    borderRadius: '50%',
                    animation: 'spin 1s linear infinite',
                    margin: '0 auto 10px'
                  }}></div>
                  Loading available time slots...
                </div>
              )}

              {/* Company Selection */}
              {!loading && companies.length > 0 && (
                <div style={{ marginBottom: '30px' }}>
                  <h3 style={{ marginBottom: '20px', color: '#f29b20' }}>Available Companies</h3>
                  <div style={{ 
                    display: 'grid', 
                    gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', 
                    gap: '20px' 
                  }}>
                    {companies.map(company => {
                      const isSelected = selectedCompanies.find(c => c._id === company._id);
                      return (
                        <div
                          key={company._id}
                          onClick={() => handleCompanySelect(company)}
                          style={{
                            ...styles.companyCard,
                            ...(isSelected && styles.companyCardSelected)
                          }}
                          onMouseOver={(e) => {
                            if (!isSelected) {
                              e.target.style.borderColor = '#f29b20';
                              e.target.style.transform = 'translateY(-2px)';
                            }
                          }}
                          onMouseOut={(e) => {
                            if (!isSelected) {
                              e.target.style.borderColor = '#333333';
                              e.target.style.transform = 'translateY(0)';
                            }
                          }}
                        >
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start', marginBottom: '10px' }}>
                            <h4 style={{ margin: 0, color: 'white' }}>{company.name}</h4>
                            <div style={{
                              width: '20px',
                              height: '20px',
                              borderRadius: '50%',
                              backgroundColor: isSelected ? '#f29b20' : 'transparent',
                              border: '2px solid #f29b20',
                              display: 'flex',
                              alignItems: 'center',
                              justifyContent: 'center'
                            }}>
                              {isSelected && <span style={{ color: '#000000', fontSize: '12px' }}>✓</span>}
                            </div>
                          </div>
                          <p style={{ margin: '5px 0', color: '#cccccc', fontSize: '0.9rem' }}>
                            Industry: {company.industry}
                          </p>
                          <p style={{ margin: '10px 0 0 0', color: 'white', fontSize: '0.9rem' }}>
                            <strong style={{color: '#f29b20'}}>Positions:</strong> {company.positions.join(', ')}
                          </p>
                          {company.description && (
                            <p style={{ margin: '10px 0 0 0', color: '#cccccc', fontSize: '0.85rem' }}>
                              {company.description}
                            </p>
                          )}
                        </div>
                      );
                    })}
                  </div>
                </div>
              )}

              {/* Time Slot Booking for Selected Companies */}
              {selectedCompanies.length > 0 && timeSlots.length > 0 && (
                <div style={{ marginBottom: '30px' }}>
                  <h3 style={{ marginBottom: '20px', color: '#f29b20' }}>
                    Book Time Slots for {formatDate(selectedDate)}
                  </h3>
                  <div style={{ display: 'flex', flexDirection: 'column', gap: '30px' }}>
                    {selectedCompanies.map(company => (
                      <div key={company._id} style={{
                        padding: '20px',
                        border: '1px solid #333333',
                        borderRadius: '12px',
                        backgroundColor: '#000000'
                      }}>
                        <h4 style={{ marginBottom: '15px', color: '#f29b20' }}>{company.name}</h4>
                        <div style={{ 
                          display: 'grid', 
                          gridTemplateColumns: 'repeat(auto-fill, minmax(120px, 1fr))', 
                          gap: '10px' 
                        }}>
                          {timeSlots.map(timeSlot => {
                            const isBooked = bookedSlots[company._id]?.[selectedDate] === timeSlot;
                            const isAvailableForCompany = availability[company._id]?.[timeSlot];
                            const isUsedByStudent = usedTimeSlots.has(timeSlot) && !isBooked;
                            const isDisabled = !isAvailableForCompany || isUsedByStudent;
                            
                            return (
                              <button
                                key={timeSlot}
                                onClick={() => !isDisabled && handleTimeSlotSelect(company._id, timeSlot)}
                                disabled={isDisabled}
                                style={{
                                  ...styles.timeSlotButton,
                                  borderColor: isBooked ? '#4ade80' : 
                                             isDisabled ? '#ff6b6b' : '#333333',
                                  backgroundColor: isBooked ? 'rgba(74, 222, 128, 0.1)' : 
                                                 isDisabled ? 'rgba(255, 107, 107, 0.1)' : '#1a1a1a',
                                  color: isBooked ? '#4ade80' : 
                                        isDisabled ? '#ff6b6b' : 'white',
                                  cursor: isDisabled ? 'not-allowed' : 'pointer',
                                  opacity: isDisabled ? 0.6 : 1
                                }}
                                onMouseOver={(e) => {
                                  if (!isDisabled) {
                                    e.target.style.borderColor = '#f29b20';
                                    e.target.style.backgroundColor = 'rgba(242, 155, 32, 0.1)';
                                  }
                                }}
                                onMouseOut={(e) => {
                                  if (!isDisabled) {
                                    e.target.style.borderColor = isBooked ? '#4ade80' : '#333333';
                                    e.target.style.backgroundColor = isBooked ? 'rgba(74, 222, 128, 0.1)' : '#1a1a1a';
                                  }
                                }}
                              >
                                {timeSlot}
                                {isBooked && ' ✓'}
                                {isDisabled && !isBooked && ' ✗'}
                              </button>
                            );
                          })}
                        </div>
                        {bookedSlots[company._id]?.[selectedDate] && (
                          <p style={{ 
                            marginTop: '10px', 
                            color: '#4ade80', 
                            fontWeight: '500',
                            fontSize: '0.9rem'
                          }}>
                            ✓ Booked: {bookedSlots[company._id][selectedDate]}
                          </p>
                        )}
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {/* Summary and Submit */}
              {Object.keys(bookedSlots).length > 0 && (
                <div style={{
                  padding: '20px',
                  backgroundColor: 'rgba(242, 155, 32, 0.1)',
                  borderRadius: '12px',
                  marginBottom: '20px',
                  border: '1px solid #f29b20'
                }}>
                  <h3 style={{ marginBottom: '15px', color: '#f29b20' }}>Appointment Summary</h3>
                  <div style={{ marginBottom: '15px', color: 'white' }}>
                    <strong>Student:</strong> {formData.firstName} {formData.lastName}<br />
                    <strong>Email:</strong> {formData.email}<br />
                    <strong>Date:</strong> {formatDate(selectedDate)}
                  </div>
                  <div>
                    <strong style={{color: '#f29b20'}}>Scheduled Appointments:</strong>
                    <ul style={{ marginTop: '10px', paddingLeft: '20px', color: 'white' }}>
                      {Object.entries(bookedSlots).map(([companyId, dates]) => {
                        const company = companies.find(c => c._id === companyId);
                        const timeSlot = dates[selectedDate];
                        return timeSlot ? (
                          <li key={companyId} style={{ marginBottom: '5px' }}>
                            <strong style={{color: '#f29b20'}}>{company?.name}</strong> - {timeSlot}
                          </li>
                        ) : null;
                      })}
                    </ul>
                  </div>
                </div>
              )}

              <div style={{ display: 'flex', gap: '20px', justifyContent: 'center' }}>
                <button
                  onClick={handleBookingSubmit}
                  disabled={Object.keys(bookedSlots).length === 0 || loading}
                  style={{
                    ...styles.button,
                    backgroundColor: Object.keys(bookedSlots).length > 0 && !loading ? '#4ade80' : '#6b7280',
                    borderColor: Object.keys(bookedSlots).length > 0 && !loading ? '#4ade80' : '#6b7280',
                    ...(Object.keys(bookedSlots).length === 0 || loading ? styles.buttonDisabled : {})
                  }}
                  onMouseOver={(e) => {
                    if (Object.keys(bookedSlots).length > 0 && !loading) {
                      e.target.style.backgroundColor = '#22c55e';
                      e.target.style.transform = 'translateY(-2px)';
                      e.target.style.boxShadow = '0 4px 15px rgba(74, 222, 128, 0.3)';
                    }
                  }}
                  onMouseOut={(e) => {
                    if (Object.keys(bookedSlots).length > 0 && !loading) {
                      e.target.style.backgroundColor = '#4ade80';
                      e.target.style.transform = 'translateY(0)';
                      e.target.style.boxShadow = 'none';
                    }
                  }}
                >
                  {loading ? 'Submitting...' :
                   Object.keys(bookedSlots).length > 0 
                    ? `Confirm ${Object.keys(bookedSlots).length} Appointment${Object.keys(bookedSlots).length > 1 ? 's' : ''}` 
                    : 'Select at least one time slot'}
                </button>
              </div>
            </>
          )}
        </div>
      )}

      {/* Instructions Panel */}
      <div style={styles.instructionsPanel}>
        <h3 style={{ marginBottom: '15px', color: '#f29b20', display: 'flex', alignItems: 'center' }}>
          <span style={{ marginRight: '10px', fontSize: '1.2rem' }}>ℹ️</span>
          How to Use This Form
        </h3>
        <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '20px' }}>
          <div>
            <h4 style={{ color: '#f29b20', marginBottom: '8px' }}>Step 1: Personal Information</h4>
            <ul style={{ paddingLeft: '20px', color: '#cccccc' }}>
              <li>Fill in your personal details</li>
              <li>Select your field of study</li>
              <li>Describe your motivation</li>
            </ul>
          </div>
          <div>
            <h4 style={{ color: '#f29b20', marginBottom: '8px' }}>Step 2: Company Selection</h4>
            <ul style={{ paddingLeft: '20px', color: '#cccccc' }}>
              <li>Choose your preferred date</li>
              <li>Select companies you want to meet</li>
              <li>Book time slots for each company</li>
            </ul>
          </div>
          <div>
            <h4 style={{ color: '#f29b20', marginBottom: '8px' }}>Important Rules</h4>
            <ul style={{ paddingLeft: '20px', color: '#cccccc' }}>
              <li>Each time slot is 15 minutes</li>
              <li>You can't book overlapping times</li>
              <li>One appointment per company per day</li>
              <li>Real-time availability checking</li>
            </ul>
          </div>
        </div>
      </div>

      {/* Add CSS animation for spinner */}
      <style>{`
        @keyframes spin {
          0% { transform: rotate(0deg); }
          100% { transform: rotate(360deg); }
        }
      `}</style>
    </div>
  );
};

export default CareerFairForm;
